import { useState } from "react";

const styles = {
  wrapper: {
    minHeight: "100vh",
    maxWidht: "100%",
    display: "flex",
    justifyContent: "center",
    alignItems: " center",
    flexDirection: "column",
  },
  h1: {
    fontSize: "6rem",
    fontWeight: "600",
    marginBottom: "2rem",
    lineHeight: "1em",
    color: "rgb(236, 236, 236)",
    textTransform: "lowercase",
    textAlign: "center",
  },
};

export default function State() {
  const [todos, setTodos] = useState([]);
  const [input, setInput] = useState("");

  const addTodo = () => {
    const newList = [...todos, input];
    setTodos(newList);
    setInput("");
  };
  const listClear = (todos, index) => {
    const list = todos.filter((x) => {
      return x !== index;
    });
    setTodos(list);
  };
  return (
    <>
      <div style={styles.wrapper}>
        <h1 style={styles.h1}>todos</h1>
        <input value={input} onChange={(e) => setInput(e.target.value)} />
        <button onClick={addTodo}>Add</button>
        <ul>
          {todos.map((todo) => (
            <li onClick={listClear} key={todo}>
              {todo}
            </li>
          ))}
        </ul>
      </div>
    </>
  );
}
