import { useState } from "react";
import { NavbarBrand } from "react-bootstrap";

const styles = {
  wrapper: {
    minHeight: "100vh",
    maxWidht: "100%",
    display: "flex",
    width: 580,
    margin: "0 auto",
    justifyContent: "center",
    alignItems: " center",
    flexDirection: "column",
  },
  h1: {
    fontSize: "6rem",
    fontWeight: "600",
    marginBottom: "2rem",
    lineHeight: "1em",
    color: "rgb(236, 236, 236)",
    textTransform: "lowercase",
    textAlign: "center",
  },
  input: {
    width: "100%",
    height: 45,
    paddingLeft: 30,
    paddingRight: 30,
    borderRadius: 50,
    boxShadow: "0 1px 6px 0 rgb(0 0 0/38%)",
    border: 0,
  },
  PlusIcon: {
    color: "darkcyan",
    fontSize: 20,
  },
  button: {
    padding: "12.5px 20px",
    lineHeight: "20px",
    margin: 0,
    background: "transparent",
    boxShadow: "none",
    border: 0,
    position: "absolute",
    right: 0,
  },
  form: {
    position: "relative",
    borderRadius: 50,
    width: "100%",
    marginBottom: 40,
  },
  ul: {
    listStyleType: "none",
    padding: 0,
    margin: 0,
    width: "100%",
  },
  li: {
    borderBottom: "1px solid #eaeaea",
    padding: "17px 0 ",
    display: "flex",
    justifyContent: "space-ground",
    fontSize: "1.2rem",
  },
  check: {
    marginRight: "15px",
  },
};

const PlusIcon = () => (
  <svg
    stroke="currentColor"
    fill="currentColor"
    stroke-width="0"
    viewBox="0 0 512 512"
    height="1em"
    width="1em"
    xmlns="http://www.w3.org/2000/svg"
    style={styles.PlusIcon}
  >
    <path d="M256 8C119 8 8 119 8 256s111 248 248 248 248-111 248-248S393 8 256 8zm144 276c0 6.6-5.4 12-12 12h-92v92c0 6.6-5.4 12-12 12h-56c-6.6 0-12-5.4-12-12v-92h-92c-6.6 0-12-5.4-12-12v-56c0-6.6 5.4-12 12-12h92v-92c0-6.6 5.4-12 12-12h56c6.6 0 12 5.4 12 12v92h92c6.6 0 12 5.4 12 12v56z"></path>
  </svg>
);

export default function State() {
  const [todos, setTodos] = useState([
    { value: "egg", checked: "true", editing: "false" },
    { value: "bread", checked: "false", editing: "false" },
    { value: "soup", checked: "true", editing: "true" },
    { value: "tsuivan", checked: "false", editing: "true" },
  ]);
  const [input, setInput] = useState("");

  const addTodo = () => {
    const newList = [...todos, input];
    setTodos(newList);
    setInput("");
  };
  const listClear = (todos, index) => {
    const list = todos.filter((x) => {
      return x !== index;
    });
    setTodos(list);
  };
  return (
    <>
      <div style={styles.wrapper}>
        <h1 style={styles.h1}>todos</h1>
        <form
          style={styles.form}
          onSubmit={(e) => {
            e.preventDefault();
            addTodo();
          }}
        >
          <input
            placeholder="Add todo .."
            style={styles.input}
            value={input}
            onChange={(e) => setInput(e.target.value)}
          />
          <button style={styles.button} onClick={addTodo}>
            <PlusIcon />
          </button>
        </form>

        <ul style={styles.ul}>
          {todos.map((todo) => (
            <li style={styles.li} onClick={listClear} key={todo}>
              <div>
                <input style={styles.check} type="checkbox" />
                {todo}
              </div>
              <div></div>
            </li>
          ))}
        </ul>
      </div>
    </>
  );
}
