import { SlTrash } from "react-icons/sl";
import { useState } from "react";

const styles = {
  wrapper: {
    minHeight: "100vh",
    display: "flex",
    width: 580,
    margin: "0 auto",
    justifyContent: "center",
    alignItems: " center",
    flexDirection: "column",
  },
  h1: {
    fontSize: "6rem",
    fontWeight: "600",
    marginBottom: "2rem",
    lineHeight: "1em",
    color: "rgb(236, 236, 236)",
    textTransform: "lowercase",
    textAlign: "center",
  },
  input: {
    width: "100%",
    height: 45,
    paddingLeft: 30,
    paddingRight: 60,
    borderRadius: 50,
    boxShadow: "0 1px 6px 0 rgb(0 0 0/38%)",
    border: 0,
  },
  PlusIcon: {
    color: "darkcyan",
    fontSize: 20,
  },
  button: {
    padding: "12.5px 20px",
    lineHeight: "20px",
    margin: 0,
    background: "transparent",
    boxShadow: "none",
    border: 0,
    position: "absolute",
    right: 0,
  },
  form: {
    position: "relative",
    width: "100%",
    marginBottom: 40,
  },
  ul: {
    listStyleType: "none",
    padding: 0,
    margin: 0,
    width: "100%",
  },
  li: {
    borderBottom: "1px solid #eaeaea",
    padding: "17px 0 ",
    display: "flex",
    justifyContent: "space-between",
    fontSize: "1.2rem",
  },
  check: {
    marginRight: 15,
  },
};

const PlusIcon = () => (
  <svg
    fill="currentColor"
    viewBox="0 0 512 512"
    height="1em"
    width="1em"
    xmlns="http://www.w3.org/2000/svg"
    style={styles.PlusIcon}
  >
    <path d="M256 8C119 8 8 119 8 256s111 248 248 248 248-111 248-248S393 8 256 8zm144 276c0 6.6-5.4 12-12 12h-92v92c0 6.6-5.4 12-12 12h-56c-6.6 0-12-5.4-12-12v-92h-92c-6.6 0-12-5.4-12-12v-56c0-6.6 5.4-12 12-12h92v-92c0-6.6 5.4-12 12-12h56c6.6 0 12 5.4 12 12v92h92c6.6 0 12 5.4 12 12v56z"></path>
  </svg>
);

export default function State() {
  const [todos, setTodos] = useState([
    { value: "egg", checked: false, editing: false },
    { value: "bread", checked: true, editing: false },
    { value: "soup", checked: false, editing: true },
  ]);
  const [input, setInput] = useState("");

  const addTodo = () => {
    const newList = [
      ...todos,
      { value: input, checked: false, editing: false },
    ];
    setTodos(newList);
    setInput("");
  };
  const removeTodo = (index) => {
    setTodos(todos.filter((k, i) => i !== index));
  };

  const handleCheck = (index) => {
    const newList = todos.map((todo, i) => {
      if (i === index) {
        const newTodo = { ...todo };
        newTodo.checked = !newTodo.checked;
        return newTodo;
      }
      return todo;
    });
    setTodos(newList);
  };

  return (
    <>
      <div style={styles.wrapper}>
        <nav>
          <h1 style={styles.h1}>todos</h1>
        </nav>
        <form
          style={styles.form}
          onSubmit={(e) => {
            e.preventDefault();
            addTodo();
          }}
        >
          <input
            placeholder="Add todo .."
            style={styles.input}
            value={input}
            onChange={(e) => setInput(e.target.value)}
          />
          <button style={styles.button} type="submit">
            <PlusIcon />
          </button>
        </form>

        <ul style={styles.ul}>
          {todos.map((todo, index) => (
            <li style={styles.li} key={`list-item-${index}`}>
              <div>
                <input
                  style={styles.check}
                  type="checkbox"
                  onheck={todo.checked}
                  onClick={() => {
                    handleCheck(index);
                  }}
                  onDoubleClick={() => {}}
                />
                <span
                  style={{
                    textDecoration: todo.checked ? " line-through" : "",
                  }}
                >
                  {todo.value}
                </span>
              </div>
              <div
                onClick={() => {
                  removeTodo(index);
                }}
              >
                <SlTrash />
              </div>
            </li>
          ))}
        </ul>
      </div>
    </>
  );
}
